    zIndex: 100,
    right: 8,
    top: 8
  },
  slideButton: {
    backgroundColor: '#F5F5F5',
    borderRadius: 8,
    marginRight: 6
  },
  slideButtonActive: {
    backgroundColor: '#1571F0',
    borderRadius: 8,
    marginRight: 6
  },
  slideTextActive: {
    paddingHorizontal: 16,
    paddingTop: 8,
    paddingBottom: 10,
    fontFamily: 'Raleway_600SemiBold',
    color: 'white',
  },
  slideText: {
    paddingHorizontal: 16,
    paddingTop: 8,
    paddingBottom: 10,
    fontFamily: 'Raleway_600SemiBold',
  }
})
