import React, { Component } from "react";
import { SafeAreaView, View, Image, Text, Modal, TouchableOpacity, TextInput, ScrollView, StyleSheet, ImageBackground, Linking } from "react-native";
import Svg, { Path, Rect } from "react-native-svg";
import Slider from "../slider/Slider";
import DesignerPageNavComponent from "./DesignerPageNav";
import BlueButton from "../../components/Component/Buttons/BlueButton";
import AsyncStorage from "@react-native-async-storage/async-storage";


export default class DesignerPageTwoComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      RewardModal: false,

      bronyModal: false,

      changed: '',
      sOpenCityDropDown: false,
      active: 0,

      user: [],
      user_bonus_for_designer: [],
      user_category_for_product: [],
      city_for_sales_user: [],
      products: [],

      categorySelect: false,

      praizvaditelSelect: false,

      getPraizvaditel: [],

      getPraizvaditelMap: [
        {
          proizvodtel_name: '',
          proizvodtel_id: '',
          proizvoditel_price: '',
          drobdown_is_open: false
        },
      ],

      praizvaditel_name: '',

      urlImage: 'http://80.78.246.59/Refectio/storage/app/uploads/',
      valid_error: false,

      categoryItems: [],

      phone: '',
      phone_error: false,
      name: '',
      name_error: false,
      dubl_phone: '',
      dubl_name: '',
      city: '',
      city_error: false,
      category_id: '',
      category_name: '',
      category_name_error: false,
      proizvaditel_info: [],
      proizvaditel_info_error: false,

      favoriteBool: false,

      VipiskaModal: false,
    }
  }


  setNewPraizvaditelPrice = async (value, index) => {
    let { getPraizvaditelMap } = this.state;
    getPraizvaditelMap[index].proizvoditel_price = value;

    await this.setState({
      getPraizvaditelMap: getPraizvaditelMap
    })
  }


  delatePraizvaditelPrice = async (index) => {
    let { getPraizvaditelMap } = this.state;
    getPraizvaditelMap.splice(index, 1)

    await this.setState({
      getPraizvaditelMap: getPraizvaditelMap
    })
  }

  addPraizvaditelPrice = async (index) => {
    let { getPraizvaditelMap } = this.state;
    getPraizvaditelMap.push({
      drobdown_is_open: false,
      proizvoditel_price: "",
      proizvodtel_id: "",
      proizvodtel_name: "",
    })

    await this.setState({
      getPraizvaditelMap: getPraizvaditelMap
    })

  }

  setNewPraizvaditelNameAndId = async (item, index) => {

    let { getPraizvaditelMap } = this.state;

    getPraizvaditelMap[index].proizvodtel_name = item.company_name;
    getPraizvaditelMap[index].proizvodtel_id = item.id;
    getPraizvaditelMap[index].drobdown_is_open = !getPraizvaditelMap[index].drobdown_is_open;


    await this.setState({
      praizvaditel_name: item.company_name,
      getPraizvaditelMap: getPraizvaditelMap
    })

  }

  toggleProizvoditelDropdown = async (index) => {

    let { getPraizvaditelMap } = this.state;

    getPraizvaditelMap[index].drobdown_is_open = !getPraizvaditelMap[index].drobdown_is_open;

    await this.setState({
      getPraizvaditelMap: getPraizvaditelMap
    })

  }



  // stexic sharunakel


  getObjectData = async () => {
    let userID = this.props.user_id
    let myHeaders = new Headers();
    let userToken = await AsyncStorage.getItem('userToken')
    let AuthStr = "Bearer " + userToken
    myHeaders.append("Authorization", AuthStr);

    await fetch('http://80.78.246.59/Refectio/public/api/getOneProizvoditel/user_id=' + userID + '/limit=2', {
      method: 'GET',
      headers: myHeaders
    })
      .then(response => response.json())
      .then(res => {
        this.setState({
          user: res.data.user,
          user_bonus_for_designer: res.data.user_bonus_for_designer,
          user_category_for_product: res.data.user_category_for_product,
          city_for_sales_user: res.data.city_for_sales_user,
          products: res.data.products,
          favoriteBool: res.data.Favorit_button
        })
      })
  }

  getCategory = async () => {
    let requestOptions = {
      method: 'GET',
      redirect: 'follow'
    };

    await fetch("http://80.78.246.59/Refectio/public/api/GetProductCategory", requestOptions)
      .then(response => response.json())
      .then(result => {
        this.setState({ categoryItems: result.data.city })
      })
      .catch(error => console.log('error', error));
  }


  DesignerAddBook = async () => {
    let myHeaders = new Headers();
    let userToken = await AsyncStorage.getItem('userToken')
    let AuthStr = "Bearer " + userToken
    myHeaders.append("Authorization", AuthStr);

    const { getPraizvaditelMap } = this.state

    let result_praizvaditel_map = []
    for (let i = 0; i < getPraizvaditelMap.length; i++) {
      result_praizvaditel_map.push(getPraizvaditelMap[i].proizvodtel_id + '^' + getPraizvaditelMap[i].proizvodtel_name + '^' + getPraizvaditelMap[i].proizvoditel_price)
    }


    let formdata = new FormData();
    formdata.append("phone", this.state.phone);
    formdata.append("name", this.state.name);
    formdata.append("dubl_phone", this.state.dubl_phone);
    formdata.append("dubl_name", this.state.dubl_name);
    formdata.append("city", this.state.city);
    formdata.append("category_id", this.state.category_id);
    formdata.append("category_name", this.state.category_name);
    formdata.append("proizvaditel_info[]", result_praizvaditel_map);

    let requestOptions = {
      method: 'POST',
      headers: myHeaders,
      body: formdata,
      redirect: 'follow'
    };

    fetch("http://80.78.246.59/Refectio/public/api/DesignerAddBook", requestOptions)
      .then(response => response.json())
      .then(result => {
        if (result.status === true && result.message[0] == 'created') {
          this.setState({ bronyModal: false })
        }
      })
      .catch(error => console.log('error', error));
  }

  sendCategoryId = async () => {
    let myHeaders = new Headers();
    let userToken = await AsyncStorage.getItem('userToken')
    let AuthStr = "Bearer " + userToken
    myHeaders.append("Authorization", AuthStr);

    let formdata = new FormData();
    formdata.append("category_id", this.state.category_id);

    let requestOptions = {
      method: 'POST',
      headers: myHeaders,
      body: formdata,
      redirect: 'follow'
    };

    fetch("http://80.78.246.59/Refectio/public/api/CetegoryForBroneProizvoditel", requestOptions)
      .then(response => response.json())
      .then(result => {
        let result_dat = []
        for (let i = 0; i < result.data.length; i++) {
          result_dat.push(result.data[i][0]);
        }
        this.setState({ getPraizvaditel: result_dat })

      })
      .catch(error => console.log('error', error));
  }


  favorite = async () => {
    let userID = this.props.user_id
    let myHeaders = new Headers();
    let userToken = await AsyncStorage.getItem('userToken')
    let AuthStr = "Bearer " + userToken
    myHeaders.append("Authorization", AuthStr);

    let formdata = new FormData();
    formdata.append("user_id", userID);

    let requestOptions = {
      method: 'POST',
      headers: myHeaders,
      body: formdata,
      redirect: 'follow'
    };


    if (this.state.favoriteBool == true) {
      fetch("http://80.78.246.59/Refectio/public/api/addtoFavorit", requestOptions)
        .then(response => response.json())
        .then(result => {
          this.setState({ favoriteBool: false })
        })
        .catch(error => console.log('error', error));
    }
    else if (this.state.favoriteBool == false) {
      fetch("http://80.78.246.59/Refectio/public/api/deleteFavoritProizvoditel", requestOptions)
        .then(response => response.json())
        .then(result => {
          this.setState({ favoriteBool: true })
        })
        .catch(error => console.log('error', error));
    }
  }



  componentDidMount() {
    const { navigation } = this.props;
    this.getObjectData()
    this.getCategory()
    this.focusListener = navigation.addListener("focus", () => {
      this.getCategory()

      this.getObjectData()

    });
  }

  componentWillUnmount() {
    // Remove the event listener
    if (this.focusListener) {
      this.focusListener();
      console.log(' END')
    }
  }

  render() {
    return (
      <SafeAreaView style={{ flex: 1, }}>
        <View style={styles.main}>

          <Modal visible={this.state.VipiskaModal}>
            <ImageBackground
              source={require('../../assets/image/blurBg.png')}
              style={{
                width: '100%',
                height: '100%',
                justifyContent: 'center',
                alignItems: 'center',
              }}>
              <View
                style={{
                  width: '90%',
                  height: 389,
                  backgroundColor: '#fff',
                  borderRadius: 20,
                  position: 'relative',

                }}>

                <TouchableOpacity
                  style={{
                    position: 'absolute',
                    width: 22.5,
                    height: 22.5,
                    right: 21.75,
                    top: 21.75,
                  }}
                  onPress={() => this.setState({ VipiskaModal: false })}>
                  <Image
                    source={require('../../assets/image/ixs.png')}
                    style={{
                      width: '100%',
                      height: '100%'
                    }}
                  />
                </TouchableOpacity>
                <Text
                  style={{
                    color: '#2D9EFB',
                    fontSize: 26,
                    marginTop: 83,
                    textAlign: 'center',
                    fontFamily: 'Poppins_600SemiBold',
                  }}>
                  Вы хотите скачать{'\n'}выписку
                </Text>
                <View style={[styles.Vipiska, { marginTop: 80 }]}>
                  <TouchableOpacity>
                    <BlueButton name='Подтвердить' />
                  </TouchableOpacity>
                  <TouchableOpacity
                    style={{
                      width: 285,
                      height: 44,
                      borderWidth: 3,
                      borderColor: '#B5D8FE',
                      borderRadius: 20,
                      justifyContent: 'center',
                      alignItems: 'center',
                      marginTop: 12
                    }}>
                    <Text
                      style={{
                        color: '#B5D8FE',
                        fontSize: 18,
                        fontFamily: 'Poppins_700Bold',
                      }}>
                      Отменить
                    </Text>
                  </TouchableOpacity>
                </View>

              </View>

            </ImageBackground>
          </Modal>



          <Modal visible={this.state.RewardModal}>
            <ImageBackground
              source={require('../../assets/image/blurBg.png')}
              style={{
                width: '100%',
                height: '100%',
                justifyContent: 'center',
                alignItems: 'center',
              }}>
              <View
                style={{
                  width: '90%',
                  // height: ,
                  backgroundColor: '#fff',
                  borderRadius: 20,
                  position: 'relative',

                }}>

                <TouchableOpacity
                  style={{ position: 'absolute', width: 22.5, height: 22.5, right: 21.75, top: 21.75, }}
                  onPress={() => this.setState({ RewardModal: false })}>
                  <Image
                    source={require('../../assets/image/ixs.png')}
                    style={{
                      width: '100%',
                      height: '100%'
                    }}
                  />
                </TouchableOpacity>
                <Text style={{ color: '#2D9EFB', fontSize: 26, marginTop: 70, textAlign: 'center', fontFamily: 'Poppins_500Medium', }}>
                  Вознаграждение
                </Text>




                < View style={styles.DesignerRemunerationPercentageParent} >
                  {
                    this.state.user_bonus_for_designer.map((item, index) => {
                      return (
                        <View style={styles.DesignerRemunerationPercentage} key={index}>

                          <Text style={styles.procentText}>От</Text>

                          <TextInput
                            editable={false}
                            keyboardType={'number-pad'}
                            style={styles.procentInput}
                            value={item.start_price}
                          />
                          <View style={styles.rubli}>
                            <Svg width="11" height="15" viewBox="0 0 11 15" fill="none" xmlns="http://www.w3.org/2000/svg">
                              <Path d="M6.285 8.99997C7.37392 9.02686 8.42909 8.62091 9.21919 7.8711C10.0093 7.1213 10.4699 6.08881 10.5 4.99997C10.4699 3.91113 10.0093 2.87865 9.21919 2.12884C8.42909 1.37904 7.37392 0.973087 6.285 0.999974H2C1.86739 0.999974 1.74021 1.05265 1.64645 1.14642C1.55268 1.24019 1.5 1.36737 1.5 1.49997V7.99997H0.5C0.367392 7.99997 0.240215 8.05265 0.146447 8.14642C0.0526785 8.24019 0 8.36736 0 8.49997C0 8.63258 0.0526785 8.75976 0.146447 8.85353C0.240215 8.9473 0.367392 8.99997 0.5 8.99997H1.5V9.99997H0.5C0.367392 9.99997 0.240215 10.0527 0.146447 10.1464C0.0526785 10.2402 0 10.3674 0 10.5C0 10.6326 0.0526785 10.7598 0.146447 10.8535C0.240215 10.9473 0.367392 11 0.5 11H1.5V14.5C1.5 14.6326 1.55268 14.7598 1.64645 14.8535C1.74021 14.9473 1.86739 15 2 15C2.13261 15 2.25979 14.9473 2.35355 14.8535C2.44732 14.7598 2.5 14.6326 2.5 14.5V11H7C7.13261 11 7.25979 10.9473 7.35355 10.8535C7.44732 10.7598 7.5 10.6326 7.5 10.5C7.5 10.3674 7.44732 10.2402 7.35355 10.1464C7.25979 10.0527 7.13261 9.99997 7 9.99997H2.5V8.99997H6.285ZM2.5 1.99997H6.285C7.10839 1.9743 7.90853 2.27531 8.51083 2.83733C9.11313 3.39935 9.46872 4.17677 9.5 4.99997C9.47001 5.82362 9.11483 6.60182 8.51223 7.16412C7.90964 7.72642 7.10875 8.02698 6.285 7.99997H2.5V1.99997Z" fill="#888888" />
                            </Svg>
                          </View>

                          <Text style={styles.procentText}>До</Text>

                          <TextInput
                            keyboardType="number-pad"
                            style={styles.procentInput}
                            editable={false}
                            value={item.before_price}
                          />

                          <View style={styles.rubli}>
                            <Svg width="11" height="15" viewBox="0 0 11 15" fill="none" xmlns="http://www.w3.org/2000/svg">
