import React, { Component } from "react";
import { SafeAreaView, View, Image, Text, TouchableOpacity, TextInput, ScrollView, StyleSheet } from "react-native";

import ArrowGrayComponent from "../../assets/image/ArrowGray";
import Svg, { Path, Rect } from "react-native-svg";
import BlueButton from "../Component/Buttons/BlueButton";
import MaskInput from 'react-native-mask-input';
import * as ImagePicker from 'expo-image-picker';




export default class RegistrationUserScreenComponent extends Component {
  constructor(props) {
    super(props)
    this.state = {
      valid: false,

      imgFirst: '',
      imgSecond: '',

      name: '',
      name_error: false,


      surname: '',
      surname_error: false,

      phone: '',
      phone_error: false,


      password: '',
      password_error: false,

      password_confirmation: '',
      password_confirmation_error: false,

      diplom_photo: null,
      selfi_photo: null,

      i_agree: false,
      i_agree_error: false,
      role_id: 2,

      accessToken: null,

    }
  }
  handleForm = (key, value) => {
    this.setState((currentForm) => ({
      ...currentForm,
      [key]: value,
    }));
  };

  form_data = new FormData();




  pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });
    if (!result.cancelled) {
      this.setState({ diplom_photo: result.uri });
    }
    let res = result.uri.split('.')
    let type = res[res.length - 1]

    this.form_data.append("diplom_photo", {
      uri: result.uri,
      type: 'image/jpg',
      name: 'photo.jpg',
    });
  };

  pickImage2 = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });

