              phone_error: true
            })
          } else {
            this.setState({
              phone_error: false
            })
          }

          if (res.data.hasOwnProperty('password')) {
            this.setState({
              password_error: true
            })
          } else {
            this.setState({
              password_error: false
            })
          }

          if (res.data.hasOwnProperty('password_confirmation')) {
            this.setState({
              password_confirmation_error: true
            })
