import React, { Component } from "react";
import { StyleSheet, SafeAreaView, View, Image, Text, Modal, TouchableOpacity } from "react-native";
import Svg, { Path, Rect } from "react-native-svg";
import BlueButton from "../Component/Buttons/BlueButton";




export default class GhostNavComponent extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      navBar: [
        { images: require('../../assets/image/home.png'), text: 'Главная', change: 'GhostPage', id: 1 },
        { images: require('../../assets/image/akar-icons_phone.png'), text: 'Поддержка', change: 'GhostPage', id: 2 },
        { images: require('../../assets/image/akar-icons_heart.png'), text: 'Избранное', change: 'Modal', id: 3 },
        { images: require('../../assets/image/carbon_user-avatar.png'), text: 'Профиль', change: 'AuthScreen', id: 4 }
      ],
      active: 0,

    }
  }

  componentDidMount = () => {

    const { navigation } = this.props;

    this.focusListener = navigation.addListener("focus", () => {

      console.log(this.state.active, 'active');

    });
  }

  componentWillUnmount() {

    // Remove the event listener

    if (this.focusListener) {
      this.focusListener();
      console.log('Bum END')
    }

    // this.focusListener();

  }

  goToPages = (e) => {
    this.props.navigation.navigate(e)
  }




  render() {
    return (
      <View style={styles.navBar}>
        {
          this.state.navBar.map((item, index) => {
            return (
              <TouchableOpacity style={{ alignItems: 'center', width: '25%', }} onPress={() => {
                this.goToPages(item.change)
              }
              } key={index}>
                <Image
                  source={item.images}
                  style={this.props.active_page == item.text ? styles.navIconsActive : styles.navIcons}
                />
                <Text key={index} style={this.props.active_page == item.text ? styles.navTextActive : styles.navText}>{item.text}</Text>
              </TouchableOpacity>
            )
          })
        }
      </View>
    )
  }
}

const styles = StyleSheet.create({
  navBar: {
    width: '100%',
    height: 50,
    flexDirection: 'row',
    justifyContent: 'space-around',
    alignItems: 'center',
    backgroundColor: '#fff',
    borderTopWidth: 3,
    borderTopColor: '#00000010'
  },
  navIcons: {
    tintColor: '#44BBEB',
    width: 25,
    height: 25,
  },
  navIconsActive: {
    tintColor: '#52A8EF',
    width: 25,
    height: 25,
  },
  navText: {
    color: '#000',
    fontSize: 10,
    fontFamily: 'Poppins_500Medium',
  },
  navTextActive: {
    color: '#52A8EF',
    fontSize: 10,
    fontFamily: 'Poppins_500Medium',
  },
})
